import React, { Component } from 'react';

class App extends Component {
  state = {
    inputValue:'this'
  }

  onType = (event) => {
    this.setState({
      inputValue: event.target.value,
    })

  }

  render () {
    return (
      <input
        type= 'text'
        onChange={this.onType}

        />
    )
  }
}

export default App;
